        xmlHttp.onreadystatechange = function() {
            if (xmlHttp.readyState === 4) {
                if (xmlHttp.status === 200) {
                    if (Chat_Id[0] === id) Chat_loadChatSuccess(xmlHttp.responseText, id);
                } else {
                    if (Chat_Id[0] === id) Chat_loadChatError(id, tryes);
                }
            }
        };

