    function Main_History_Sort(array, msort, direction) {

        if (direction) { //a-z
            array.sort(
                function(a, b) {
                    return (a[msort] < b[msort] ? -1 : (a[msort] > b[msort] ? 1 : 0));
                }
            );
        } else { //z-a
            array.sort(
                function(a, b) {
                    return (a[msort] > b[msort] ? -1 : (a[msort] < b[msort] ? 1 : 0));
                }
            );
        }
    }

