    function Settings_GenerateClock() {
        var clock = [],
            time = 43200,
            i = 0;

        for (i; i < 48; i++) {
            clock.push("-" + Play_timeS(time));
            time -= 900;
        }

        clock.push(Play_timeS(0));
        time = 900;

        for (i = 0; i < 48; i++) {
            clock.push(Play_timeS(time));
            time += 900;
        }

        return clock;
    }

    function Settings_GetVolumes() {
        var array = [],
            i = 0;

        for (i = 0; i < 101; i++) {
            array.push(i + '%');
        }

        return array;
    }

    function Settings_GetnotificationTime() {
        var array = [0, '10 min', '20 min', '30 min', '45 min', '1 Hour'],
            i = 0;

        for (i = 2; i < 25; i++) {
            array.push(i + ' Hours');
        }

        return array;
    }

